function intializePlayer(){vid=document.getElementById("mainVideo");playbtn=document.getElementById("playPausebtn");clickplay=document.getElementById("mainVideo");slider=document.getElementById("slider");curtimetext=document.getElementById("curtimetext");durtimetext=document.getElementById("durtimetext");mutebtn=document.getElementById("mutebtn");fullscreenbtn=document.getElementById("fullscreenbtn");playbtn.addEventListener("click",playPause,false);clickplay.addEventListener("click",clickPlay,false);slider.addEventListener("change",vidSeek,false);vid.addEventListener("timeupdate",seektimeupdate,false);mutebtn.addEventListener("click",vidmute,false);fullscreenbtn.addEventListener("click",toggleFullScreen,false)}function playPause(){if(vid.paused){vid.play();playbtn.innerHTML="<i class='icon-pause'></i>"}else{vid.pause();playbtn.innerHTML="<i class='icon-play'></i>"}}function clickPlay(){if(vid.paused){vid.play();playbtn.innerHTML="<i class='icon-pause'></i>"}else{vid.pause();playbtn.innerHTML="<i class='icon-play'></i>"}}function vidSeek(){var e=vid.duration*(slider.value/100);vid.currentTime=e}function seektimeupdate(){var e=vid.currentTime*(100/vid.duration);slider.value=e;var t=Math.floor(vid.currentTime/60);var n=Math.floor(vid.currentTime-t*60);var r=Math.floor(vid.duration/60);var i=Math.floor(vid.duration-r*60);if(n<10){n="0"+n}if(i<10){i="0"+i}if(t<10){t="0"+t}if(r<10){r="0"+r}curtimetext.innerHTML=t+":"+n;durtimetext.innerHTML=r+":"+i}function vidmute(){if(vid.muted){vid.muted=false;mutebtn.innerHTML="<i class='icon-unmute'></i>"}else{vid.muted=true;mutebtn.innerHTML="<i class='icon-mute'></i>"}}function toggleFullScreen(){if(vid.requestFullScreen){vid.requestFullScreen()}else if(vid.webkitRequestFullScreen){vid.webkitRequestFullScreen()}else if(vid.mozRequestFullScreen){vid.mozRequestFullScreen()}}var vid;var playbtn;var playbtn;var clickplay;var slider;var curtimetext;var durtimetext;var mutebtn;var fullscreenbtn;window.onload=intializePlayer
